pragma solidity 0.6.12;
pragma experimental ABIEncoderV2;

/// @dev All factors or APYs are written as a number with mantissa 18.
struct AssetMetadata {
    address assetAddress;
    string assetSymbol;
    uint8 assetDecimals;
    uint256 referencePrice;
    uint256 totalLiquidity;
    uint256 totalSupply;
    uint256 totalBorrow;
    uint256 totalReserves;
    uint256 supplyAPR;
    uint256 borrowAPR;
    address rewardTokenAddress;
    string rewardTokenSymbol;
    uint8 rewardTokenDecimals;
    uint256 estimatedSupplyRewardsPerYear;
    uint256 estimatedBorrowRewardsPerYear;
    uint256 collateralFactor;
    uint256 liquidationFactor;
    bool canSupply;
    bool canBorrow;
}

interface ILendingPlatform {
    function getAssetMetadata(address platform, address asset) external returns (AssetMetadata memory assetMetadata);

    function getCollateralUsageFactor(address platform) external returns (uint256 collateralUsageFactor);

    function getCollateralFactorForAsset(address platform, address asset) external returns (uint256);

    function getReferencePrice(address platform, address token) external returns (uint256 referencePrice);

    function getBorrowBalance(address platform, address token) external returns (uint256 borrowBalance);

    function getSupplyBalance(address platform, address token) external returns (uint256 supplyBalance);

    function claimRewards(address platform) external returns (address rewardsToken, uint256 rewardsAmount);

    function enterMarkets(address platform, address[] memory markets) external;

    function supply(
        address platform,
        address token,
        uint256 amount
    ) external;

    function borrow(
        address platform,
        address token,
        uint256 amount
    ) external;

    function redeemSupply(
        address platform,
        address token,
        uint256 amount
    ) external;

    function repayBorrow(
        address platform,
        address token,
        uint256 amount
    ) external;
}

contracts/Fodl/modules/PNL/PNLStorage.so.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.6.12;
pragma experimental ABIEncoderV2;

/**
 * priceTarget:             when the assets reach this price ratio, position can be closed
 *
 *
 * reward:                  when the bot repays the debt, it gets paid with this amount of supply
 *
 *
 * unwindFactor:            percentage of debt that can be repaid when the position is
 *                          eligible for take profit
 */
struct PNLSettings {
    uint256 priceTarget;
    uint256 fixedReward;
    uint256 percentageReward;
    uint256 unwindFactor;
    bool isTakeProfit;
}

contract PNLStorage {
    bytes32 constant TAKE_PROFIT_LIMIT_STORAGE_POSITION = keccak256('folding.storage.pnl');

    struct PNLStore {
        PNLSettings[] pnlSettings;
    }

    function pnlStore() internal pure returns (PNLStore storage s) {
        bytes32 position = TAKE_PROFIT_LIMIT_STORAGE_POSITION;
        assembly {
            s_slot := position
        }
    }
}

