// SPDX-License-Identifier: MIT

pragma solidity 0.6.12;
pragma experimental ABIEncoderV2;

import '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import '@openzeppelin/contracts/token/ERC20/SafeERC20.sol';
import '@openzeppelin/contracts/math/SafeMath.sol';

import './Interfaces.sol';
import '../ILendingPlatform.sol';
import '../../../modules/FoldingAccount/FoldingAccountStorage.sol';
import '../../../modules/SimplePosition/SimplePositionStorage.sol';

contract AaveLendingAdapter is ILendingPlatform, SimplePositionStorage {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    address public immutable PoolProvider; // IAaveLendingPoolProvider(0xB53C1a33016B2DC2fF3653530bfF1848a515c8c5);
    address public immutable DataProvider; // IAaveDataProvider(0x057835Ad21a177dbdd3090bB1CAE03EaCF78Fc6d);
    address public immutable Incentives; // IAaveIncentivesController(0xd784927Ff2f95ba542BfC824c8a8a98F3495f6b5);

    uint256 private constant VARIABLE_BORROW_RATE_MODE = 2;
    uint256 private constant SECONDS_PER_YEAR = 365 * 24 * 60 * 60;

    constructor(
        address _aavePoolProvider,
        address _aaveData,
        address _aaveIncentives
    ) public {
        require(_aavePoolProvider != address(0), 'ICP0');
        require(_aaveData != address(0), 'ICP0');
        require(_aaveIncentives != address(0), 'ICP0');
        PoolProvider = _aavePoolProvider;
        DataProvider = _aaveData;
        Incentives = _aaveIncentives;
    }

    function getCollateralUsageFactor(address) external override returns (uint256) {
        address aave = IAaveLendingPoolProvider(PoolProvider).getLendingPool();
        (, , , , , uint256 hF) = IAaveLendingPool(aave).getUserAccountData(address(this));
        return uint256(1e36) / hF;
    }

    /**
     * @dev reasoning:
     * 1) get asset reserve data, which has liquidation threshold encoded in bits 16-31
     * 2) right shift the number so that we remove bits 0-15
     * 3) now with modulo 2**16, the retrieved value is the first 16 bits of the data, which is the liquiditation
     * threshold for the asset according to "struct ReserveConfigurationMap" (see Interfaces.sol).
     * This number goes from 0 (0%) to 10000 (100%). To transform it to mantissa 18, this number is multiplied by 1e14.
     **/
    function getCollateralFactorForAsset(address, address asset) external override returns (uint256 collateralFactor) {
        return
            ((IAaveLendingPool(IAaveLendingPoolProvider(PoolProvider).getLendingPool())
                .getReserveData(asset)
                .configuration
                .data >> 16) % (2**16)) * 1e14;
    }

    /**
     * @dev reasoning:
     * Aave reference prices do not take into account the number of decimals of the token, but our system does.
     * To take them into account, we multiply by ETH's decimals (1e18) and divide by the token 's decimals.
     **/
    function getReferencePrice(address, address token) public override returns (uint256) {
        return
            IAavePriceOracleGetter(IAaveLendingPoolProvider(PoolProvider).getPriceOracle())
                .getAssetPrice(token)
                .mul(1e18)
                .div(10**uint256(ERC20(token).decimals()));
    }

    function getBorrowBalance(address, address token) external override returns (uint256 borrowBalance) {
        (, , borrowBalance, , , , , , ) = IAaveDataProvider(DataProvider).getUserReserveData(token, address(this));
    }

    function getSupplyBalance(address, address token) external override returns (uint256 supplyBalance) {
        (supplyBalance, , , , , , , , ) = IAaveDataProvider(DataProvider).getUserReserveData(token, address(this));
    }

    function claimRewards(address) public override returns (address rewardsToken, uint256 rewardsAmount) {
        rewardsToken = IAaveIncentivesController(Incentives).REWARD_TOKEN();
        uint256 before = IERC20(rewardsToken).balanceOf(address(this));

        address[] memory assets = new address[](2);
        (assets[0], , ) = IAaveDataProvider(DataProvider).getReserveTokensAddresses(simplePositionStore().supplyToken);
        (, , assets[1]) = IAaveDataProvider(DataProvider).getReserveTokensAddresses(simplePositionStore().borrowToken);

        IAaveIncentivesController(Incentives).claimRewards(assets, type(uint256).max, address(this));
        rewardsAmount = IERC20(rewardsToken).balanceOf(address(this)).sub(before);
    }

    /// @dev Empty because this is done by default in Aave
    function enterMarkets(address, address[] memory markets) external override {}

    function supply(
        address,
        address token,
        uint256 amount
    ) external override {
        address aave = IAaveLendingPoolProvider(PoolProvider).getLendingPool();
        IERC20(token).safeIncreaseAllowance(aave, amount);
        IAaveLendingPool(aave).deposit(token, amount, address(this), 0);
    }

    function borrow(
        address,
        address token,
        uint256 amount
    ) external override {
        address aave = IAaveLendingPoolProvider(PoolProvider).getLendingPool();
        IAaveLendingPool(aave).borrow(token, amount, VARIABLE_BORROW_RATE_MODE, 0, address(this));
    }

    function redeemSupply(
        address,
        address token,
        uint256 amount
    ) external override {
        address aave = IAaveLendingPoolProvider(PoolProvider).getLendingPool();
        IAaveLendingPool(aave).withdraw(token, amount, address(this));
    }

    function repayBorrow(
        address,
        address token,
        uint256 amount
    ) external override {
        address aave = IAaveLendingPoolProvider(PoolProvider).getLendingPool();
        IERC20(token).safeIncreaseAllowance(address(aave), amount);
        IAaveLendingPool(aave).repay(token, amount, VARIABLE_BORROW_RATE_MODE, address(this));
    }

    /// @dev Aave uses ray precision for APRs (i.e. 1e27) while we use 1e18
    function convertFromRayToE18(uint256 factorRay) private pure returns (uint256 factorE18) {
        factorE18 = factorRay / 1e9;
    }

    /// @dev Aave uses E4 precision for factors while we use 1e18
    function convertFromE4ToE18(uint256 factorE4) private pure returns (uint256 factorE18) {
        factorE18 = factorE4.mul(1e14);
    }

    struct AssetDetails {
        uint256 ltv;
        uint256 liquidationThreshold;
        uint256 availableLiquidity;
        uint256 totalStableDebt;
        uint256 totalVariableDebt;
        uint256 liquidityRate;
        uint256 variableBorrowRate;
        uint256 supplyEmissionPerSec;
        uint256 borrowEmissionPerSec;
        bool borrowingEnabled;
        bool isActive;
        bool isFrozen;
    }

    function getAssetDetails(address asset) private view returns (AssetDetails memory assetDetails) {
        (
            ,
            assetDetails.ltv,
            assetDetails.liquidationThreshold,
            ,
            ,
            ,
            assetDetails.borrowingEnabled,
            ,
            assetDetails.isActive,
            assetDetails.isFrozen
        ) = IAaveDataProvider(DataProvider).getReserveConfigurationData(asset);

        (
            assetDetails.availableLiquidity,
            assetDetails.totalStableDebt,
            assetDetails.totalVariableDebt,
            assetDetails.liquidityRate,
            assetDetails.variableBorrowRate,
            ,
            ,
            ,
            ,

        ) = IAaveDataProvider(DataProvider).getReserveData(asset);

        (address aTokenAddress, , address vDebtTokenAddress) = IAaveDataProvider(DataProvider)
            .getReserveTokensAddresses(asset);

        (, assetDetails.supplyEmissionPerSec, ) = IAaveIncentivesController(Incentives).getAssetData(aTokenAddress);
        (, assetDetails.borrowEmissionPerSec, ) = IAaveIncentivesController(Incentives).getAssetData(vDebtTokenAddress);
    }

    function getAssetMetadata(address, address asset) external override returns (AssetMetadata memory assetMetadata) {
        AssetDetails memory data = getAssetDetails(asset);

        assetMetadata.assetAddress = asset;
        assetMetadata.assetSymbol = ERC20(asset).symbol();
        assetMetadata.assetDecimals = ERC20(asset).decimals();
        assetMetadata.referencePrice = getReferencePrice(address(0), asset);
        assetMetadata.totalLiquidity = data.availableLiquidity;
        assetMetadata.totalBorrow = data.totalStableDebt.add(data.totalVariableDebt);
        assetMetadata.totalSupply = assetMetadata.totalBorrow.add(assetMetadata.totalLiquidity);
        assetMetadata.totalReserves = 0; // Aave reserves are not relevant towards computation
        assetMetadata.supplyAPR = convertFromRayToE18(data.liquidityRate);
        assetMetadata.borrowAPR = convertFromRayToE18(data.variableBorrowRate);
        assetMetadata.rewardTokenAddress = IAaveIncentivesController(Incentives).REWARD_TOKEN();
        assetMetadata.rewardTokenDecimals = ERC20(assetMetadata.rewardTokenAddress).decimals();
        assetMetadata.rewardTokenSymbol = ERC20(assetMetadata.rewardTokenAddress).symbol();
        assetMetadata.estimatedSupplyRewardsPerYear = data.supplyEmissionPerSec.mul(SECONDS_PER_YEAR);
        assetMetadata.estimatedBorrowRewardsPerYear = data.borrowEmissionPerSec.mul(SECONDS_PER_YEAR);
        assetMetadata.collateralFactor = convertFromE4ToE18(data.ltv);
        assetMetadata.liquidationFactor = convertFromE4ToE18(data.liquidationThreshold);
        assetMetadata.canSupply = data.isActive && !data.isFrozen;
        assetMetadata.canBorrow = data.isActive && data.borrowingEnabled;
    }
}

contracts/Fodl/modules/Lender/Aave/Inter.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.6.12;
pragma experimental ABIEncoderV2;

library DataTypes {
    // refer to the whitepaper, section 1.1 basic concepts for a formal description of these properties.
    struct ReserveData {
        //stores the reserve configuration
        ReserveConfigurationMap configuration;
        //the liquidity index. Expressed in ray
        uint128 liquidityIndex;
        //variable borrow index. Expressed in ray
        uint128 variableBorrowIndex;
        //the current supply rate. Expressed in ray
        uint128 currentLiquidityRate;
        //the current variable borrow rate. Expressed in ray
        uint128 currentVariableBorrowRate;
        //the current stable borrow rate. Expressed in ray
        uint128 currentStableBorrowRate;
        uint40 lastUpdateTimestamp;
        //tokens addresses
        address aTokenAddress;
        address stableDebtTokenAddress;
        address variableDebtTokenAddress;
        //address of the interest rate strategy
        address interestRateStrategyAddress;
        //the id of the reserve. Represents the position in the list of the active reserves
        uint8 id;
    }

    struct ReserveConfigurationMap {
        //bit 0-15: LTV
        //bit 16-31: Liq. threshold
        //bit 32-47: Liq. bonus
        //bit 48-55: Decimals
        //bit 56: Reserve is active
        //bit 57: reserve is frozen
        //bit 58: borrowing is enabled
        //bit 59: stable rate borrowing enabled
        //bit 60-63: reserved
        //bit 64-79: reserve factor
        uint256 data;
    }

    struct UserConfigurationMap {
        uint256 data;
    }

    enum InterestRateMode {
        NONE,
        STABLE,
        VARIABLE
    }
}

interface IAaveLendingPool {
    /**
     * @dev Emitted on deposit()
     * @param reserve The address of the underlying asset of the reserve
     * @param user The address initiating the deposit
     * @param onBehalfOf The beneficiary of the deposit, receiving the aTokens
     * @param amount The amount deposited
     * @param referral The referral code used
     **/
    event Deposit(
        address indexed reserve,
        address user,
        address indexed onBehalfOf,
        uint256 amount,
        uint16 indexed referral
    );

    /**
     * @dev Emitted on withdraw()
     * @param reserve The address of the underlyng asset being withdrawn
     * @param user The address initiating the withdrawal, owner of aTokens
     * @param to Address that will receive the underlying
     * @param amount The amount to be withdrawn
     **/
    event Withdraw(address indexed reserve, address indexed user, address indexed to, uint256 amount);

    /**
     * @dev Emitted on borrow() and flashLoan() when debt needs to be opened
     * @param reserve The address of the underlying asset being borrowed
     * @param user The address of the user initiating the borrow(), receiving the funds on borrow() or just
     * initiator of the transaction on flashLoan()
     * @param onBehalfOf The address that will be getting the debt
     * @param amount The amount borrowed out
     * @param borrowRateMode The rate mode: 1 for Stable, 2 for Variable
     * @param borrowRate The numeric rate at which the user has borrowed
     * @param referral The referral code used
     **/
    event Borrow(
        address indexed reserve,
        address user,
        address indexed onBehalfOf,
        uint256 amount,
        uint256 borrowRateMode,
        uint256 borrowRate,
        uint16 indexed referral
    );

    /**
     * @dev Emitted on repay()
     * @param reserve The address of the underlying asset of the reserve
     * @param user The beneficiary of the repayment, getting his debt reduced
     * @param repayer The address of the user initiating the repay(), providing the funds
     * @param amount The amount repaid
     **/
    event Repay(address indexed reserve, address indexed user, address indexed repayer, uint256 amount);

    /**
     * @dev Emitted on swapBorrowRateMode()
     * @param reserve The address of the underlying asset of the reserve
     * @param user The address of the user swapping his rate mode
     * @param rateMode The rate mode that the user wants to swap to
     **/
    event Swap(address indexed reserve, address indexed user, uint256 rateMode);

    /**
     * @dev Emitted on setUserUseReserveAsCollateral()
     * @param reserve The address of the underlying asset of the reserve
     * @param user The address of the user enabling the usage as collateral
     **/
    event ReserveUsedAsCollateralEnabled(address indexed reserve, address indexed user);

    /**
     * @dev Emitted on setUserUseReserveAsCollateral()
     * @param reserve The address of the underlying asset of the reserve
     * @param user The address of the user enabling the usage as collateral
     **/
    event ReserveUsedAsCollateralDisabled(address indexed reserve, address indexed user);

    /**
     * @dev Emitted on rebalanceStableBorrowRate()
     * @param reserve The address of the underlying asset of the reserve
     * @param user The address of the user for which the rebalance has been executed
     **/
    event RebalanceStableBorrowRate(address indexed reserve, address indexed user);

    /**
     * @dev Emitted on flashLoan()
     * @param target The address of the flash loan receiver contract
     * @param initiator The address initiating the flash loan
     * @param asset The address of the asset being flash borrowed
     * @param amount The amount flash borrowed
     * @param premium The fee flash borrowed
     * @param referralCode The referral code used
     **/
    event FlashLoan(
        address indexed target,
        address indexed initiator,
        address indexed asset,
        uint256 amount,
        uint256 premium,
        uint16 referralCode
    );

    /**
     * @dev Emitted when the pause is triggered.
     */
    event Paused();

    /**
     * @dev Emitted when the pause is lifted.
     */
    event Unpaused();

    /**
     * @dev Emitted when a borrower is liquidated. This event is emitted by the LendingPool via
     * LendingPoolCollateral manager using a DELEGATECALL
     * This allows to have the events in the generated ABI for LendingPool.
     * @param collateralAsset The address of the underlying asset used as collateral, to receive as result of the liquidation
     * @param debtAsset The address of the underlying borrowed asset to be repaid with the liquidation
     * @param user The address of the borrower getting liquidated
     * @param debtToCover The debt amount of borrowed `asset` the liquidator wants to cover
     * @param liquidatedCollateralAmount The amount of collateral received by the liiquidator
     * @param liquidator The address of the liquidator
     * @param receiveAToken `true` if the liquidators wants to receive the collateral aTokens, `false` if he wants
     * to receive the underlying collateral asset directly
     **/
    event LiquidationCall(
        address indexed collateralAsset,
        address indexed debtAsset,
        address indexed user,
        uint256 debtToCover,
        uint256 liquidatedCollateralAmount,
        address liquidator,
        bool receiveAToken
    );

    /**
     * @dev Emitted when the state of a reserve is updated. NOTE: This event is actually declared
     * in the ReserveLogic library and emitted in the updateInterestRates() function. Since the function is internal,
     * the event will actually be fired by the LendingPool contract. The event is therefore replicated here so it
     * gets added to the LendingPool ABI
     * @param reserve The address of the underlying asset of the reserve
     * @param liquidityRate The new liquidity rate
     * @param stableBorrowRate The new stable borrow rate
     * @param variableBorrowRate The new variable borrow rate
     * @param liquidityIndex The new liquidity index
     * @param variableBorrowIndex The new variable borrow index
     **/
    event ReserveDataUpdated(
        address indexed reserve,
        uint256 liquidityRate,
        uint256 stableBorrowRate,
        uint256 variableBorrowRate,
        uint256 liquidityIndex,
        uint256 variableBorrowIndex
    );

    /**
     * @dev Deposits an `amount` of underlying asset into the reserve, receiving in return overlying aTokens.
     * - E.g. User deposits 100 USDC and gets in return 100 aUSDC
     * @param asset The address of the underlying asset to deposit
     * @param amount The amount to be deposited
     * @param onBehalfOf The address that will receive the aTokens, same as msg.sender if the user
     *   wants to receive them on his own wallet, or a different address if the beneficiary of aTokens
     *   is a different wallet
     * @param referralCode Code used to register the integrator originating the operation, for potential rewards.
     *   0 if the action is executed directly by the user, without any middle-man
     **/
    function deposit(
        address asset,
        uint256 amount,
        address onBehalfOf,
        uint16 referralCode
    ) external;

    /**
     * @dev Withdraws an `amount` of underlying asset from the reserve, burning the equivalent aTokens owned
     * E.g. User has 100 aUSDC, calls withdraw() and receives 100 USDC, burning the 100 aUSDC
     * @param asset The address of the underlying asset to withdraw
     * @param amount The underlying amount to be withdrawn
     *   - Send the value type(uint256).max in order to withdraw the whole aToken balance
     * @param to Address that will receive the underlying, same as msg.sender if the user
     *   wants to receive it on his own wallet, or a different address if the beneficiary is a
     *   different wallet
     * @return The final amount withdrawn
     **/
    function withdraw(
        address asset,
        uint256 amount,
        address to
    ) external returns (uint256);

    /**
     * @dev Allows users to borrow a specific `amount` of the reserve underlying asset, provided that the borrower
     * already deposited enough collateral, or he was given enough allowance by a credit delegator on the
     * corresponding debt token (StableDebtToken or VariableDebtToken)
     * - E.g. User borrows 100 USDC passing as `onBehalfOf` his own address, receiving the 100 USDC in his wallet
     *   and 100 stable/variable debt tokens, depending on the `interestRateMode`
     * @param asset The address of the underlying asset to borrow
     * @param amount The amount to be borrowed
     * @param interestRateMode The interest rate mode at which the user wants to borrow: 1 for Stable, 2 for Variable
     * @param referralCode Code used to register the integrator originating the operation, for potential rewards.
     *   0 if the action is executed directly by the user, without any middle-man
     * @param onBehalfOf Address of the user who will receive the debt. Should be the address of the borrower itself
     * calling the function if he wants to borrow against his own collateral, or the address of the credit delegator
     * if he has been given credit delegation allowance
     **/
    function borrow(
        address asset,
        uint256 amount,
        uint256 interestRateMode,
        uint16 referralCode,
        address onBehalfOf
    ) external;

    /**
     * @notice Repays a borrowed `amount` on a specific reserve, burning the equivalent debt tokens owned
     * - E.g. User repays 100 USDC, burning 100 variable/stable debt tokens of the `onBehalfOf` address
     * @param asset The address of the borrowed underlying asset previously borrowed
     * @param amount The amount to repay
     * - Send the value type(uint256).max in order to repay the whole debt for `asset` on the specific `debtMode`
     * @param rateMode The interest rate mode at of the debt the user wants to repay: 1 for Stable, 2 for Variable
     * @param onBehalfOf Address of the user who will get his debt reduced/removed. Should be the address of the
     * user calling the function if he wants to reduce/remove his own debt, or the address of any other
     * other borrower whose debt should be removed
     * @return The final amount repaid
     **/
    function repay(
        address asset,
        uint256 amount,
        uint256 rateMode,
        address onBehalfOf
    ) external returns (uint256);

    /**
     * @dev Allows a borrower to swap his debt between stable and variable mode, or viceversa
     * @param asset The address of the underlying asset borrowed
     * @param rateMode The rate mode that the user wants to swap to
     **/
    function swapBorrowRateMode(address asset, uint256 rateMode) external;

    /**
     * @dev Rebalances the stable interest rate of a user to the current stable rate defined on the reserve.
     * - Users can be rebalanced if the following conditions are satisfied:
     *     1. Usage ratio is above 95%
     *     2. the current deposit APY is below REBALANCE_UP_THRESHOLD * maxVariableBorrowRate, which means that too much has been
     *        borrowed at a stable rate and depositors are not earning enough
     * @param asset The address of the underlying asset borrowed
     * @param user The address of the user to be rebalanced
     **/
    function rebalanceStableBorrowRate(address asset, address user) external;

    /**
     * @dev Allows depositors to enable/disable a specific deposited asset as collateral
     * @param asset The address of the underlying asset deposited
     * @param useAsCollateral `true` if the user wants to use the deposit as collateral, `false` otherwise
     **/
    function setUserUseReserveAsCollateral(address asset, bool useAsCollateral) external;

    /**
     * @dev Function to liquidate a non-healthy position collateral-wise, with Health Factor below 1
     * - The caller (liquidator) covers `debtToCover` amount of debt of the user getting liquidated, and receives
     *   a proportionally amount of the `collateralAsset` plus a bonus to cover market risk
     * @param collateralAsset The address of the underlying asset used as collateral, to receive as result of the liquidation
     * @param debtAsset The address of the underlying borrowed asset to be repaid with the liquidation
     * @param user The address of the borrower getting liquidated
     * @param debtToCover The debt amount of borrowed `asset` the liquidator wants to cover
     * @param receiveAToken `true` if the liquidators wants to receive the collateral aTokens, `false` if he wants
     * to receive the underlying collateral asset directly
     **/
    function liquidationCall(
        address collateralAsset,
        address debtAsset,
        address user,
        uint256 debtToCover,
        bool receiveAToken
    ) external;

    /**
     * @dev Allows smartcontracts to access the liquidity of the pool within one transaction,
     * as long as the amount taken plus a fee is returned.
     * IMPORTANT There are security concerns for developers of flashloan receiver contracts that must be kept into consideration.
     * For further details please visit https://developers.aave.com
     * @param receiverAddress The address of the contract receiving the funds, implementing the IFlashLoanReceiver interface
     * @param assets The addresses of the assets being flash-borrowed
     * @param amounts The amounts amounts being flash-borrowed
     * @param modes Types of the debt to open if the flash loan is not returned:
     *   0 -> Don't open any debt, just revert if funds can't be transferred from the receiver
     *   1 -> Open debt at stable rate for the value of the amount flash-borrowed to the `onBehalfOf` address
     *   2 -> Open debt at variable rate for the value of the amount flash-borrowed to the `onBehalfOf` address
     * @param onBehalfOf The address  that will receive the debt in the case of using on `modes` 1 or 2
     * @param params Variadic packed params to pass to the receiver as extra information
     * @param referralCode Code used to register the integrator originating the operation, for potential rewards.
     *   0 if the action is executed directly by the user, without any middle-man
     **/
    function flashLoan(
        address receiverAddress,
        address[] calldata assets,
        uint256[] calldata amounts,
        uint256[] calldata modes,
        address onBehalfOf,
        bytes calldata params,
        uint16 referralCode
    ) external;

    /**
     * @dev Returns the user account data across all the reserves
     * @param user The address of the user
     * @return totalCollateralETH the total collateral in ETH of the user
     * @return totalDebtETH the total debt in ETH of the user
     * @return availableBorrowsETH the borrowing power left of the user
     * @return currentLiquidationThreshold the liquidation threshold of the user
     * @return ltv the loan to value of the user
     * @return healthFactor the current health factor of the user
     **/
    function getUserAccountData(address user)
        external
        view
        returns (
            uint256 totalCollateralETH,
            uint256 totalDebtETH,
            uint256 availableBorrowsETH,
            uint256 currentLiquidationThreshold,
            uint256 ltv,
            uint256 healthFactor
        );

    function initReserve(
        address reserve,
        address aTokenAddress,
        address stableDebtAddress,
        address variableDebtAddress,
        address interestRateStrategyAddress
    ) external;

    function setReserveInterestRateStrategyAddress(address reserve, address rateStrategyAddress) external;

    function setConfiguration(address reserve, uint256 configuration) external;

    /**
     * @dev Returns the configuration of the reserve
     * @param asset The address of the underlying asset of the reserve
     * @return The configuration of the reserve
     **/
    function getConfiguration(address asset) external view returns (DataTypes.ReserveConfigurationMap memory);

    /**
     * @dev Returns the configuration of the user across all the reserves
     * @param user The user address
     * @return The configuration of the user
     **/
    function getUserConfiguration(address user) external view returns (DataTypes.UserConfigurationMap memory);

    /**
     * @dev Returns the normalized income normalized income of the reserve
     * @param asset The address of the underlying asset of the reserve
     * @return The reserve's normalized income
     */
    function getReserveNormalizedIncome(address asset) external view returns (uint256);

    /**
     * @dev Returns the normalized variable debt per unit of asset
     * @param asset The address of the underlying asset of the reserve
     * @return The reserve normalized variable debt
     */
    function getReserveNormalizedVariableDebt(address asset) external view returns (uint256);

    /**
     * @dev Returns the state and configuration of the reserve
     * @param asset The address of the underlying asset of the reserve
     * @return The state of the reserve
     **/
    function getReserveData(address asset) external view returns (DataTypes.ReserveData memory);

    function finalizeTransfer(
        address asset,
        address from,
        address to,
        uint256 amount,
        uint256 balanceFromAfter,
        uint256 balanceToBefore
    ) external;

    function getReservesList() external view returns (address[] memory);

    function getAddressesProvider() external view returns (IAaveLendingPoolProvider);

    function setPause(bool val) external;

    function paused() external view returns (bool);
}

interface IAaveLendingPoolProvider {
    function owner() external view returns (address);

    function getMarketId() external view returns (string memory);

    function setMarketId(string calldata marketId) external;

    function setAddress(bytes32 id, address newAddress) external;

    function setAddressAsProxy(bytes32 id, address impl) external;

    function getAddress(bytes32 id) external view returns (address);

    function getLendingPool() external view returns (address);

    function setLendingPoolImpl(address pool) external;

    function getLendingPoolConfigurator() external view returns (address);

    function setLendingPoolConfiguratorImpl(address configurator) external;

    function getLendingPoolCollateralManager() external view returns (address);

    function setLendingPoolCollateralManager(address manager) external;

    function getPoolAdmin() external view returns (address);

    function setPoolAdmin(address admin) external;

    function getEmergencyAdmin() external view returns (address);

    function setEmergencyAdmin(address admin) external;

    function getPriceOracle() external view returns (address);

    function setPriceOracle(address priceOracle) external;

    function getLendingRateOracle() external view returns (address);

    function setLendingRateOracle(address lendingRateOracle) external;
}

interface IAaveDataProvider {
    function getReserveTokensAddresses(address _asset)
        external
        view
        returns (
            address aTokenAddress,
            address stableDebtTokenAddress,
            address variableDebtTokenAddress
        );

    function getUserReserveData(address _asset, address _user)
        external
        view
        returns (
            uint256 currentATokenBalance,
            uint256 currentStableDebt,
            uint256 currentVariableDebt,
            uint256 principalStableDebt,
            uint256 scaledVariableDebt,
            uint256 stableBorrowRate,
            uint256 liquidityRate,
            uint40 stableRateLastUpdated,
            bool usageAsCollateralEnabled
        );

    struct TokenData {
        string symbol;
        address tokenAddress;
    }

    function getAllATokens() external view returns (TokenData[] memory);

    function getAllReservesTokens() external view returns (TokenData[] memory);

    function getReserveConfigurationData(address asset)
        external
        view
        returns (
            uint256 decimals,
            uint256 ltv,
            uint256 liquidationThreshold,
            uint256 liquidationBonus,
            uint256 reserveFactor,
            bool usageAsCollateralEnabled,
            bool borrowingEnabled,
            bool stableBorrowRateEnabled,
            bool isActive,
            bool isFrozen
        );

    function getReserveData(address asset)
        external
        view
        returns (
            uint256 availableLiquidity,
            uint256 totalStableDebt,
            uint256 totalVariableDebt,
            uint256 liquidityRate,
            uint256 variableBorrowRate,
            uint256 stableBorrowRate,
            uint256 averageStableBorrowRate,
            uint256 liquidityIndex,
            uint256 variableBorrowIndex,
            uint40 lastUpdateTimestamp
        );
}

interface ATokenInterface {
    function balanceOf(address _user) external view returns (uint256);
}

interface IAavePriceOracleGetter {
    function getAssetPrice(address _asset) external view returns (uint256);

    function getAssetsPrices(address[] calldata _assets) external view returns (uint256[] memory);

    function getSourceOfAsset(address _asset) external view returns (address);

    function getFallbackOracle() external view returns (address);
}

interface IAaveIncentivesController {
    /**
     * @dev Whitelists an address to claim the rewards on behalf of another address
     * @param user The address of the user
     * @param claimer The address of the claimer
     */
    function setClaimer(address user, address claimer) external;

    /**
     * @dev Returns the whitelisted claimer for a certain address (0x0 if not set)
     * @param user The address of the user
     * @return The claimer address
     */
    function getClaimer(address user) external view returns (address);

    /**
     * @dev Configure assets for a certain rewards emission
     * @param assets The assets to incentivize
     * @param emissionsPerSecond The emission for each asset
     */
    function configureAssets(address[] calldata assets, uint256[] calldata emissionsPerSecond) external;

    /**
     * @dev Called by the corresponding asset on any update that affects the rewards distribution
     * @param asset The address of the user
     * @param userBalance The balance of the user of the asset in the lending pool
     * @param totalSupply The total supply of the asset in the lending pool
     **/
    function handleAction(
        address asset,
        uint256 userBalance,
        uint256 totalSupply
    ) external;

    /**
     * @dev Returns the total of rewards of an user, already accrued + not yet accrued
     * @param user The address of the user
     * @return The rewards
     **/
    function getRewardsBalance(address[] calldata assets, address user) external view returns (uint256);

    /**
     * @dev Claims reward for an user, on all the assets of the lending pool, accumulating the pending rewards
     * @param amount Amount of rewards to claim
     * @param to Address that will be receiving the rewards
     * @return Rewards claimed
     **/
    function claimRewards(
        address[] calldata assets,
        uint256 amount,
        address to
    ) external returns (uint256);

    /**
     * @dev Claims reward for an user on behalf, on all the assets of the lending pool, accumulating the pending rewards. The caller must
     * be whitelisted via "allowClaimOnBehalf" function by the RewardsAdmin role manager
     * @param amount Amount of rewards to claim
     * @param user Address to check and claim rewards
     * @param to Address that will be receiving the rewards
     * @return Rewards claimed
     **/
    function claimRewardsOnBehalf(
        address[] calldata assets,
        uint256 amount,
        address user,
        address to
    ) external returns (uint256);

    /**
     * @dev returns the unclaimed rewards of the user
     * @param user the address of the user
     * @return the unclaimed user rewards
     */
    function getUserUnclaimedRewards(address user) external view returns (uint256);

    /**
     * @dev for backward compatibility with previous implementation of the Incentives controller
     */
    function REWARD_TOKEN() external view returns (address);

    /**
     * @dev Sets the end date for the distribution
     * @param distributionEnd The end date timestamp
     **/
    function setDistributionEnd(uint256 distributionEnd) external;

    /**
     * @dev Gets the end date for the distribution
     * @return The end of the distribution
     **/
    function getDistributionEnd() external view returns (uint256);

    /**
     * @dev for backwards compatibility with the previous DistributionManager used
     * @return The end of the distribution
     **/
    function DISTRIBUTION_END() external view returns (uint256);

    /**
     * @dev Returns the data of an user on a distribution
     * @param user Address of the user
     * @param asset The address of the reference asset of the distribution
     * @return The new index
     **/
    function getUserAssetData(address user, address asset) external view returns (uint256);

    /**
     * @dev Returns the configuration of the distribution for a certain asset
     * @param asset The address of the reference asset of the distribution
     * @return The asset index, the emission per second and the last updated timestamp
     **/
    function getAssetData(address asset)
        external
        view
        returns (
            uint256,
            uint256,
            uint256
        );
}

contracts/Fodl/connectors/SimplePosition.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.6.12;
pragma experimental ABIEncoderV2;

import '@openzeppelin/contracts/math/SafeMath.sol';
import '@openzeppelin/contracts/token/ERC20/SafeERC20.sol';

import '../interfaces/IWhitelistPNLConnector.sol';
import '../../../Fodl/modules/SimplePosition/SimplePositionStorage.sol';
import '../../../Fodl/modules/Lender/LendingDispatcher.sol';
import '../../../Fodl/modules/PNL/PNLStorage.sol';
import '../../modules/RiskManagement/RiskManagementStorage.sol';

contract WhitelistPNLConnector is
    SimplePositionStorage,
    LendingDispatcher,
    PNLStorage,
    RiskManagementStorage,
    IWhitelistPNLConnector
{
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    uint256 private constant MANTISSA = 1e18;

    function configurePNL(
        uint256 priceTarget,
        uint256 fixedReward,
        uint256 percentageReward,
        uint256 unwindFactor,
        address permittedBot,
        bool isTakeProfit
    ) external override onlyAccountOwner {
        require(isSimplePosition(), 'SP1');
        require(unwindFactor <= MANTISSA, 'WPNL1');
        require(percentageReward <= MANTISSA, 'WPNL2');

        SimplePositionStore memory sp = simplePositionStore();
        address lender = getLender(sp.platform);

        if (isTakeProfit) {
            require(
                priceTarget >
                    getReferencePrice(lender, sp.platform, sp.supplyToken).mul(MANTISSA) /
                        getReferencePrice(lender, sp.platform, sp.borrowToken),
                'WPNL3'
            );
        } else {
            require(
                priceTarget <
                    getReferencePrice(lender, sp.platform, sp.supplyToken).mul(MANTISSA) /
                        getReferencePrice(lender, sp.platform, sp.borrowToken),
                'WPNL3'
            );
        }

        if (permittedBot != address(0)) riskMagamentStore().whitelist[permittedBot] = true;

        pnlStore().pnlSettings.push(
            PNLSettings(priceTarget, fixedReward, percentageReward, unwindFactor, isTakeProfit)
        );
    }

    function setPNLWhitelistPermission(address addr, bool permission) external override onlyAccountOwner {
        riskMagamentStore().whitelist[addr] = permission;
    }

    function getPNLWhitelistPermission(address addr) external override returns (bool) {
        return riskMagamentStore().whitelist[addr];
    }

    function removePNLSetting(uint256 index) external override onlyAccountOwner {
        removePNLInternal(index);
    }

    function removeAllPNLSettings() external override onlyAccountOwner {
        delete pnlStore().pnlSettings;
    }

    function executePNL(uint256 index, bool withApproval) external override returns (uint256) {
        require(riskMagamentStore().whitelist[tx.origin] == true, 'WPNL4');
        PNLSettings memory configuration = pnlStore().pnlSettings[index];
        removePNLInternal(index);
        SimplePositionStore memory sp = simplePositionStore();

        address platform = sp.platform;
        address supplyToken = sp.supplyToken;
        address borrowToken = sp.borrowToken;
        address lender = getLender(platform);
        uint256 priceOfSupplyToken = getReferencePrice(lender, platform, supplyToken);
        uint256 priceOfBorrowToken = getReferencePrice(lender, platform, borrowToken);

        if (configuration.isTakeProfit) {
            require(priceOfSupplyToken.mul(MANTISSA) / priceOfBorrowToken >= configuration.priceTarget, 'WPNL5');
        } else {
            require(priceOfSupplyToken.mul(MANTISSA) / priceOfBorrowToken <= configuration.priceTarget, 'WPNL5');
        }

        uint256 repayAmount = configuration.unwindFactor.mul(getBorrowBalance(lender, platform, borrowToken)) /
            MANTISSA;

        uint256 redeemAmount = (repayAmount.mul(priceOfBorrowToken.mul(MANTISSA + configuration.percentageReward)) /
            (priceOfSupplyToken.mul(MANTISSA))).add(configuration.fixedReward);

        address caller = aStore().entryCaller;
        if (withApproval) IERC20(borrowToken).safeTransferFrom(caller, address(this), repayAmount);
        repayBorrow(lender, platform, borrowToken, repayAmount);
        redeemSupply(lender, platform, supplyToken, redeemAmount);
        IERC20(supplyToken).safeTransfer(caller, redeemAmount);
        return redeemAmount;
    }

    /// @dev    simply deleting the storage at the index is not enough,
    ///         as it would leave an empty gap in the array. We need to move the
    ///         targeted element to the tail, and then pop.
    function removePNLInternal(uint256 index) internal {
        PNLStore storage store = pnlStore();
        uint256 length = store.pnlSettings.length;
        require(index < length, 'WPNL6');

        if (index != length - 1) {
            store.pnlSettings[index] = store.pnlSettings[length - 1];
        }

        store.pnlSettings.pop();
    }

    function getAllPNLSettings() external override returns (PNLSettings[] memory) {
        return pnlStore().pnlSettings;
    }

    function getPNLSettingsAt(uint256 index) external override returns (PNLSettings memory) {
        return pnlStore().pnlSettings[index];
    }

    function getPNLState() external override returns (PNLState memory state) {
        SimplePositionStore memory sp = simplePositionStore();
        if (!isSimplePosition()) return state;

        PNLSettings[] memory settings = pnlStore().pnlSettings;

        state.platform = sp.platform;
        state.supplyToken = sp.supplyToken;
        state.borrowToken = sp.borrowToken;

        state.supplyBalance = getSupplyBalance(getLender(sp.platform), sp.platform, sp.supplyToken);
        state.borrowBalance = getBorrowBalance(getLender(sp.platform), sp.platform, sp.borrowToken);

        state.priceOfBorrowToken = getReferencePrice(getLender(sp.platform), sp.platform, sp.borrowToken);
        state.priceOfSupplyToken = getReferencePrice(getLender(sp.platform), sp.platform, sp.supplyToken);
        state.simulations = new PNLSimulations[](settings.length);

        uint256 currentPrice = state.priceOfSupplyToken.mul(MANTISSA) / state.priceOfBorrowToken;

        for (uint256 i = 0; i < settings.length; i++) {
            uint256 repayAmount = settings[i].unwindFactor.mul(state.borrowBalance) / MANTISSA;
            uint256 redeemAmount = (repayAmount.mul(
                state.priceOfBorrowToken.mul(MANTISSA + settings[i].percentageReward)
            ) / (state.priceOfSupplyToken.mul(MANTISSA))).add(settings[i].fixedReward);

            string memory reason;
            bool canBeTriggered = true;

            if (
                settings[i].isTakeProfit
                    ? currentPrice < settings[i].priceTarget
                    : currentPrice > settings[i].priceTarget
            ) {
                canBeTriggered = false;
                reason = 'Price target not reached';
            } else if (redeemAmount > state.supplyBalance) {
                canBeTriggered = false;
                reason = 'Incentive exceeds supply balance';
            }

            state.simulations[i] = PNLSimulations(canBeTriggered, reason, settings[i]);
        }
    }
}

